import bcrypt from "bcrypt";
import { db } from "../server/db";
import { users, institutions } from "../shared/schema";
import { eq } from "drizzle-orm";

async function createAdminUsers() {
  console.log("ðŸ” Creating admin users...");

  try {
    // Get Institut BitÃ cola
    const [institution] = await db.select().from(institutions).where(eq(institutions.name, "Institut BitÃ cola"));

    if (!institution) {
      console.error("âŒ Institut BitÃ cola not found. Please run seeding first.");
      return;
    }

    // Create superadmin user
    const superAdminPassword = await bcrypt.hash("admin123", 10);
    const [superAdmin] = await db.insert(users).values({
      id: crypto.randomUUID(),
      email: "admin@bitacola.edu",
      firstName: "Super",
      lastName: "Admin",
      passwordHash: superAdminPassword,
      role: "superadmin",
      institutionId: institution.id
    }).onConflictDoUpdate({
      target: users.email,
      set: {
        passwordHash: superAdminPassword,
        role: "superadmin",
        updatedAt: new Date()
      }
    }).returning();

    // Create admin user
    const adminPassword = await bcrypt.hash("admin123", 10);
    const [admin] = await db.insert(users).values({
      id: crypto.randomUUID(),
      email: "director@bitacola.edu",
      firstName: "Maria",
      lastName: "Director",
      passwordHash: adminPassword,
      role: "admin", 
      institutionId: institution.id
    }).onConflictDoUpdate({
      target: users.email,
      set: {
        passwordHash: adminPassword,
        role: "admin",
        updatedAt: new Date()
      }
    }).returning();

    // Create employee user  
    const employeePassword = await bcrypt.hash("employee123", 10);
    const [employee] = await db.insert(users).values({
      id: crypto.randomUUID(),
      email: "profesor@bitacola.edu",
      firstName: "Joan",
      lastName: "Professor",
      passwordHash: employeePassword,
      role: "employee",
      institutionId: institution.id
    }).onConflictDoUpdate({
      target: users.email,
      set: {
        passwordHash: employeePassword,
        role: "employee", 
        updatedAt: new Date()
      }
    }).returning();

    console.log("âœ… Admin users created successfully:");
    console.log(`
ðŸ”‘ LOGIN CREDENTIALS:

ðŸ‘‘ SUPERADMIN:
   Email: admin@bitacola.edu
   Password: admin123

ðŸ‘” ADMIN (Director):
   Email: director@bitacola.edu
   Password: admin123

ðŸ‘¨â€ðŸ« EMPLOYEE (Professor):
   Email: profesor@bitacola.edu  
   Password: employee123

ðŸŽ¯ Use these credentials to test the authentication system!
    `);

  } catch (error) {
    console.error("âŒ Error creating admin users:", error);
  }
}

// Run the script
createAdminUsers();

