  /* Enhanced card styles */
  .card-modern {
    @apply bg-rose-50/50 dark:bg-slate-800/50 border border-rose-100 dark:border-slate-600 rounded-xl shadow-sm 
    hover:shadow-md hover:bg-rose-50/70 dark:hover:bg-slate-700/70 transition-all duration-200;
  }

  /* Modal and Dialog fixes - COMPLETE TRANSPARENCY ELIMINATION */

  /* FORCE SOLID BACKGROUNDS FOR ALL RADIX COMPONENTS */
  [data-radix-dialog-content],
  [data-radix-dropdown-menu-content],
  [data-radix-context-menu-content],
  [data-radix-popover-content],
  [data-radix-hover-card-content],
  [data-radix-select-content],
  [data-radix-alert-dialog-content],
  .vaul-drawer-wrapper {
