  }

  .dark [data-radix-dialog-content],
  .dark [data-radix-dropdown-menu-content], 
  .dark [data-radix-context-menu-content],
  .dark [data-radix-popover-content],
  .dark [data-radix-hover-card-content],
  .dark [data-radix-select-content],
