  }

  /* FORCE SOLID OVERLAYS */
  [data-radix-dialog-overlay],
  [data-radix-alert-dialog-overlay] {
    background: rgba(0, 0, 0, 0.90) !important;
    backdrop-filter: none !important;
  }

  /* SPECIFIC MODAL TRANSPARENCY FIXES */
  .modal-content-solid {
    background: rgb(248 250 252) !important;
    border: 2px solid rgb(226 232 240) !important;
    backdrop-filter: none !important;
  }

  .dark .modal-content-solid {
    background: rgb(15 23 42) !important;
    border: 2px solid rgb(51 65 85) !important;
  }

  .select-content-solid {
    background: rgb(248 250 252) !important;
    border: 2px solid rgb(226 232 240) !important;
  }

  .dark .select-content-solid {
    background: rgb(15 23 42) !important;
    border: 2px solid rgb(51 65 85) !important;
  }

  [role="dialog"] {
    @apply bg-background border shadow-2xl !important;
    z-index: 9999 !important;
  }

  [data-radix-popper-content-wrapper] {
    z-index: 9999 !important;
  }

  .dialog-overlay {
    @apply fixed inset-0 bg-black/60 backdrop-blur-sm z-50 !important;
  }

  .dialog-content {
    @apply fixed left-1/2 top-1/2 z-50 grid w-full max-w-lg -translate-x-1/2 -translate-y-1/2
    gap-4 border bg-background p-6 shadow-2xl duration-200 rounded-xl !important;
  }

  /* Status indicators */
  .status-online {
    @apply w-3 h-3 bg-green-500 rounded-full animate-pulse;
  }

  .status-offline {
    @apply w-3 h-3 bg-gray-400 rounded-full;
  }

  /* Status badges */
  .status-active {
