import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { 
  Dialog, 
  DialogContent, 
  DialogHeader, 
  DialogTitle, 
  DialogDescription,
  DialogFooter
} from "@/components/ui/dialog";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Calendar, 
  Check, 
  X, 
  MessageCircle, 
  Clock,
  User,
  FileText
} from "lucide-react";

interface AbsenceJustificationReviewProps {
  institutionId: string | null | undefined;
  language: string;
}

interface AbsenceJustification {
  id: string;
  employeeId: string;
  employeeName: string;
  date: string;
  reason: string;
  status: 'pending' | 'approved' | 'rejected';
  adminResponse?: string;
  createdAt: string;
}

export default function AbsenceJustificationReview({ institutionId, language }: AbsenceJustificationReviewProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [selectedJustification, setSelectedJustification] = useState<AbsenceJustification | null>(null);
  const [showReviewModal, setShowReviewModal] = useState(false);
  const [adminResponse, setAdminResponse] = useState("");
  const [reviewAction, setReviewAction] = useState<'approve' | 'reject' | null>(null);

  // Fetch pending absence justifications
  const { data: justifications = [], isLoading } = useQuery<AbsenceJustification[]>({
    queryKey: ["/api/absence-justifications/admin", institutionId],
    queryFn: async () => {
      const response = await fetch(`/api/absence-justifications/admin/${institutionId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch absence justifications');
      }
      const data = await response.json();
      // Transform the data to match expected interface
      return data.map((absence: any) => ({
        id: absence.id,
        employeeId: absence.employeeId,
        employeeName: absence.employeeName,
        date: absence.startDate, // Map startDate to date
        reason: absence.reason,
        status: absence.justificationStatus === 'pending' ? 'pending' : 
                absence.justificationStatus === 'approved' ? 'approved' : 
                absence.justificationStatus === 'rejected' ? 'rejected' : 'pending',
        adminResponse: absence.adminResponse,
        createdAt: absence.createdAt,
      }));
    },
    enabled: !!institutionId,
  });

  // Review justification mutation
  const reviewMutation = useMutation({
    mutationFn: async (data: { id: string; status: string; adminResponse: string }) => {
      return await apiRequest("PUT", `/api/absence-justifications/${data.id}/status`, {
        status: data.status,
        adminResponse: data.adminResponse
      });
    },
    onSuccess: (data, variables) => {
      toast({
