      {/* Help Text */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
        <h4 className="font-medium text-yellow-800 mb-2">
          {language === "ca" ? "Exemples de configuraciÃ³:" : "Ejemplos de configuraciÃ³n:"}
        </h4>
        <ul className="text-sm text-yellow-700 space-y-1">
          <li>â€¢ <code>192.168.1.0/24</code> - {language === "ca" ? "Tota la xarxa local" : "Toda la red local"}</li>
          <li>â€¢ <code>192.168.1.100</code> - {language === "ca" ? "IP especÃ­fica" : "IP especÃ­fica"}</li>
          <li>â€¢ <code>10.0.0.0/8</code> - {language === "ca" ? "Xarxa privada gran" : "Red privada grande"}</li>
