  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (formData.name && formData.startDate && formData.endDate && institutionId) {
      createMutation.mutate({
        ...formData,
        institutionId
      } as InsertAcademicYear);
    }
