                  <TableHead>{language === "ca" ? "Data de Fi" : "Fecha de Fin"}</TableHead>
                  <TableHead>{language === "ca" ? "Estat" : "Estado"}</TableHead>
                  <TableHead>{language === "ca" ? "Durada" : "DuraciÃ³n"}</TableHead>
                  <TableHead>{t("actions", language)}</TableHead>
                </TableRow>
