                  <TableHead>
                    {language === "ca" ? "Empleat" : "Empleado"}
                  </TableHead>
                  <TableHead>
                    {language === "ca" ? "Tipus" : "Tipo"}
                  </TableHead>
                  <TableHead>
                    {t("status", language)}
                  </TableHead>
                  <TableHead>
                    {t("actions", language)}
                  </TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {alertHistory.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={5} className="text-center py-8 text-muted-foreground">
                      {language === "ca" ? "No hi ha historial d'alertes" : "No hay historial de alertas"}
                    </TableCell>
                  </TableRow>
                ) : (
                  alertHistory.map((alert: any) => (
                    <TableRow key={alert.id}>
                      <TableCell>
                        {new Date(alert.createdAt).toLocaleString(language === "ca" ? "ca-ES" : "es-ES")}
                      </TableCell>
                      <TableCell>
                        {alert.employeeName || 'N/A'}
                      </TableCell>
                      <TableCell>
                        <Badge variant={alert.type === 'late_arrival' ? 'secondary' : 'destructive'}>
                          {getAlertTitle(alert.type)}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <Badge variant={alert.status === 'resolved' ? 'secondary' : (alert.emailSent ? 'default' : 'outline')}>
                          {alert.status === 'resolved' ? 
                            (language === "ca" ? "Resolta" : "Resuelta") : 
                            (alert.emailSent ? 
                              (language === "ca" ? "Enviat" : "Enviado") : 
                              (language === "ca" ? "Pendent" : "Pendiente")
                            )
                          }
                        </Badge>
                      </TableCell>
                      <TableCell>
                        {alert.status === 'resolved' ? (
                          <div className="text-sm text-muted-foreground">
                            {language === "ca" ? "Resolta per" : "Resuelta por"} {alert.resolvedByName}
                            <br />
                            {new Date(alert.resolvedAt).toLocaleString(language === "ca" ? "ca-ES" : "es-ES")}
                          </div>
                        ) : (
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => resolveAlertMutation.mutate(alert.id)}
                            disabled={resolveAlertMutation.isPending}
                          >
                            {language === "ca" ? "Resoldre" : "Resolver"}
                          </Button>
                        )}
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>
    </main>
  );
}

