        doc.setFontSize(10);
        doc.setFont('helvetica', 'normal');
        doc.text(`Document ID: ${data.document_info.export_id}`, margin, yPosition);
        yPosition += 8;
        doc.text(`Data d'exportaciÃ³: ${new Date(data.document_info.export_date).toLocaleString('ca-ES')}`, margin, yPosition);
        yPosition += 8;
        doc.text(`Usuari: ${data.document_info.user_name}`, margin, yPosition);
