        }

        // Legal Information
        yPosition += 15;
        doc.setFontSize(12);
        doc.setFont('helvetica', 'bold');
        doc.text('INFORMACIÃ“ LEGAL', margin, yPosition);
        yPosition += 10;

        doc.setFontSize(10);
        doc.setFont('helvetica', 'normal');
        doc.text(`Base legal: ${data.legal_info.legal_basis}`, margin, yPosition);
        yPosition += 8;
        doc.text(`Article RGPD: ${data.legal_info.gdpr_article}`, margin, yPosition);
        yPosition += 8;
        doc.text(`PerÃ­ode de conservaciÃ³: ${data.legal_info.retention_period}`, margin, yPosition);

        // Audit Trail
        yPosition += 15;
        doc.setFontSize(12);
