import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLanguage } from "@/hooks/useLanguage";
import { useAuth } from "@/hooks/useAuth";
import { t } from "@/lib/i18n";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import { 
  FolderInput, 
  Plus, 
  Search, 
  Edit, 
  Clock, 
  Trash2,
  User
} from "lucide-react";
import EmployeeModal from "@/components/modals/EmployeeModal";
import EmployeeDetailModal from "@/components/EmployeeDetailModal";
import type { Employee } from "@shared/schema";

export default function Employees() {
  const { language } = useLanguage();
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [searchQuery, setSearchQuery] = useState("");
  const [departmentFilter, setDepartmentFilter] = useState("all");
  const [statusFilter, setStatusFilter] = useState("all");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState<Employee | null>(null);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);
  const [viewingEmployee, setViewingEmployee] = useState<any | null>(null);

  // Get institution ID from authenticated user
  const institutionId = user?.institutionId;

  const { data: employees = [], isLoading } = useQuery<Employee[]>({
    queryKey: ["/api/admin/employees", institutionId, searchQuery],
    queryFn: async () => {
      console.log('EMPLOYEES: Fetching employees for admin, institution:', institutionId);
      if (!institutionId) {
        throw new Error('No institution ID available');
      }
      const response = await fetch(`/api/admin/employees/${institutionId}`, {
        credentials: 'include'
      });
      if (!response.ok) {
        throw new Error('Failed to fetch employees');
      }
      const data = await response.json();
      console.log('EMPLOYEES: Received employees data:', data);
      return data;
    },
    enabled: !!institutionId,
  });

  const deleteEmployeeMutation = useMutation({
    mutationFn: async (employeeId: string) => {
      await apiRequest("DELETE", `/api/employees/${employeeId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/employees"] });
      toast({
        title: t("success", language),
        description: language === "ca" ? "Empleat eliminat correctament" : "Empleado eliminado correctamente",
      });
    },
    onError: () => {
      toast({
        title: t("error", language),
        description: language === "ca" ? "Error eliminant l'empleat" : "Error eliminando el empleado",
        variant: "destructive",
      });
    },
  });

  const handleEdit = (employee: Employee) => {
    setEditingEmployee(employee);
    setIsModalOpen(true);
  };

  const handleDelete = (employee: Employee) => {
    if (confirm(language === "ca" ? 
      `EstÃ s segur que vols eliminar ${employee.fullName}?` : 
      `Â¿EstÃ¡s seguro que quieres eliminar ${employee.fullName}?`)) {
      deleteEmployeeMutation.mutate(employee.id);
    }
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
    setEditingEmployee(null);
  };

  const handleViewDetails = (employee: any) => {
    setViewingEmployee(employee);
    setIsDetailModalOpen(true);
  };

  const handleDetailModalClose = () => {
    setIsDetailModalOpen(false);
    setViewingEmployee(null);
  };

  const getStatusBadge = (status: string) => {
    const statusMap = {
      active: { 
        label: t("active", language), 
        variant: "default" as const,
        className: "bg-secondary/10 text-secondary"
      },
      inactive: { 
        label: t("inactive", language), 
        variant: "secondary" as const,
        className: "bg-gray-100 text-gray-600"
      },
      temporary_leave: { 
        label: t("temporary_leave", language), 
        variant: "outline" as const,
        className: "bg-accent/10 text-accent"
      },
    };

    const statusInfo = statusMap[status as keyof typeof statusMap] || statusMap.active;

    return (
      <Badge variant={statusInfo.variant} className={statusInfo.className}>
        {statusInfo.label}
      </Badge>
    );
  };

  const getContractTypeLabel = (type: string) => {
    const typeMap = {
      full_time: t("full_time", language),
      part_time: t("part_time", language),
      substitute: t("substitute", language),
    };
    return typeMap[type as keyof typeof typeMap] || type;
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="h-32 bg-gray-200 rounded"></div>
          <div className="h-96 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <main className="p-6">
      {/* Header */}
