import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { 
  BarChart3, 
  FileText, 
  TrendingUp,
  Users,
  Clock,
  AlertTriangle,
  LoaderIcon,
  FileDown,
  Calendar,
  CheckCircle,
  XCircle,
  MinusCircle
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  BarChart,
  Bar
} from 'recharts';

export default function Reports() {
  const { language } = useLanguage();
  const { user } = useAuth();
  const permissions = usePermissions();
  const { toast } = useToast();

  // Estados simplificados
  const [selectedReportTypes, setSelectedReportTypes] = useState<string[]>(["general_attendance"]);
  const [startDate, setStartDate] = useState("2025-01-01");
  const [endDate, setEndDate] = useState("2025-01-31");
  const [isLoading, setIsLoading] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [reportData, setReportData] = useState<any>(null);
  const [detailedAttendance, setDetailedAttendance] = useState<any[]>([]);
  const [monthlyTrends, setMonthlyTrends] = useState<any[]>([]);

  // Initialize dates and generate initial report (only once)
  const [hasInitialized, setHasInitialized] = useState(false);

  useEffect(() => {
    if (user?.institutionId && !hasInitialized) {
      const now = new Date();
      const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);

      const startDateStr = lastMonth.toISOString().split('T')[0];
      const endDateStr = now.toISOString().split('T')[0];

      console.log("ðŸ“… Setting initial dates:", startDateStr, "to", endDateStr);
      setStartDate(startDateStr);
      setEndDate(endDateStr);
      setHasInitialized(true);

      // Generate initial report
      generateInitialReport(startDateStr, endDateStr);
    }
  }, [user?.institutionId, hasInitialized]);

  // Simple function to generate initial report without dependencies
  const generateInitialReport = async (start: string, end: string) => {
    if (!user?.institutionId) return;

    console.log("ðŸš€ Generating initial report for:", start, "to", end);
    setIsLoading(true);

    try {
      const [overviewResponse, detailResponse, trendsResponse] = await Promise.all([
        fetch(`/api/reports/overview/${user.institutionId}?startDate=${start}&endDate=${end}`, {
          credentials: 'include'
        }),
        fetch(`/api/reports/detailed-attendance/${user.institutionId}?startDate=${start}&endDate=${end}`, {
          credentials: 'include'
        }),
        fetch(`/api/reports/monthly-trends/${user.institutionId}`, {
          credentials: 'include'
        })
      ]);

      if (overviewResponse.ok && detailResponse.ok && trendsResponse.ok) {
        const overview = await overviewResponse.json();
        const details = await detailResponse.json();
        const trends = await trendsResponse.json();

        setReportData(overview);
        setDetailedAttendance(details);
        setMonthlyTrends(trends);

        console.log("âœ… Initial report generated successfully");
      }
    } catch (error) {
      console.error("âŒ Error generating initial report:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const reportTypes = [
    { 
      value: "general_attendance", 
      label: language === "ca" ? "AssistÃ¨ncia general" : "Asistencia general" 
    },
    { 
      value: "late_absences", 
      label: language === "ca" ? "Retards i absÃ¨ncies" : "Retrasos y ausencias" 
    },
    { 
      value: "worked_hours", 
      label: language === "ca" ? "Hores treballades" : "Horas trabajadas" 
    },
  ];

  // Manejar selecciÃ³n de tipos de informe (checkboxes)
  const handleReportTypeToggle = (reportType: string) => {
    console.log("ðŸ”„ Toggling report type:", reportType);

    setSelectedReportTypes(prevTypes => {
      const isCurrentlySelected = prevTypes.includes(reportType);

      if (isCurrentlySelected) {
        // Solo permitir deseleccionar si hay mÃ¡s de un tipo seleccionado
        if (prevTypes.length > 1) {
          return prevTypes.filter(type => type !== reportType);
        } else {
          // Mantener al menos un tipo seleccionado
          console.log("âš ï¸ Keeping at least one type selected");
          return prevTypes;
        }
