        description: language === "ca" ? "No s'ha pogut exportar" : "No se pudo exportar",
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  // Exportar PDF simplificado (prÃ³ximamente)
  const handleExportPDF = () => {
    console.log("ðŸ“„ PDF export requested");
