          {language === "ca" 
            ? "Genera informes detallats de l'assistÃ¨ncia i exporta-los en diferents formats"
            : "Genera informes detallados de asistencia y expÃ³rtalos en diferentes formatos"
          }
        </p>
      </header>

      {/* Configuration Card */}
      <Card data-testid="report-config-card">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <BarChart3 className="h-5 w-5" />
            {language === "ca" ? "ConfiguraciÃ³ de l'informe" : "ConfiguraciÃ³n del informe"}
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Report Type Selection with Checkboxes */}
          <div>
            <Label className="text-base font-medium mb-3 block">
              {language === "ca" ? "Tipus d'informes" : "Tipos de informes"}
            </Label>
            <div className="space-y-3 p-4 bg-gray-50 rounded-lg border">
              {reportTypes.map((type) => (
                <div key={type.value} className="flex items-center space-x-3 p-2 hover:bg-white rounded border-l-4 border-transparent hover:border-blue-400 transition-all">
