  Users, 
  Book, 
  Clock, 
  CheckCircle, 
  AlertTriangle,
  Download,
  Copy,
  RefreshCw,
  AlertCircle,
  Info,
  Settings
} from "lucide-react";
import ProtectedRoute from "@/components/ProtectedRoute";
import { UntisStats } from "@/components/UntisStats";

export default function ScheduleImport() {
  const { language } = useLanguage();
  const { user } = useAuth();
  const permissions = usePermissions();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [selectedFileName, setSelectedFileName] = useState<string>("");
  const [importProgress, setImportProgress] = useState(0);
  const [isImporting, setIsImporting] = useState(false);
  const [previewData, setPreviewData] = useState<any>(null);
  const [selectedAcademicYear, setSelectedAcademicYear] = useState("");
  const [importResults, setImportResults] = useState<any>(null);

  // Get academic years for current institution
  const { data: academicYears = [] } = useQuery({
    queryKey: ["/api/academic-years", user?.institutionId],
    enabled: !!user?.institutionId,
    staleTime: 60000, // Dades vÃ lides per 1 minut per evitar re-renders innecessaris
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    console.log('FILE_SELECT: File selected:', file?.name, file?.type);

    if (file) {
      const fileName = file.name.toLowerCase();
      if (fileName.endsWith('.xml') || fileName.endsWith('.gpu') || fileName.endsWith('.txt')) {
