import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertTriangle, Send, User, Clock, Calendar } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useLanguage } from "@/hooks/useLanguage";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface RiskAssessment {
  id: string;
  employeeId: string;
  employeeName: string;
  employeeEmail: string;
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  delayMinutes: number;
  absenceDays: number;
  lastCalculated: string;
  notes?: string;
}

export default function RiskAssessmentDashboard() {
  const { user } = useAuth();
  const { language } = useLanguage();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedEmployee, setSelectedEmployee] = useState<RiskAssessment | null>(null);
  const [notificationMessage, setNotificationMessage] = useState("");
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);

  // Fetch risk assessments
  const { data: riskAssessments, isLoading } = useQuery({
    queryKey: [`/api/admin/risk-assessments/${user?.institutionId}`],
    enabled: !!user?.institutionId,
  });

  // Send manual notification mutation
  const sendNotificationMutation = useMutation({
    mutationFn: async (data: { employeeId: string; message: string }) => {
      return await apiRequest("POST", "/api/admin/send-notification", data);
    },
    onSuccess: () => {
      toast({
        title: language === "ca" ? "NotificaciÃ³ enviada" : "NotificaciÃ³n enviada",
        description: language === "ca" ? "La notificaciÃ³ s'ha enviat correctament" : "La notificaciÃ³n se ha enviado correctamente",
      });
      setIsNotificationOpen(false);
      setNotificationMessage("");
      setSelectedEmployee(null);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || (language === "ca" ? "Error enviant la notificaciÃ³" : "Error enviando la notificaciÃ³n"),
        variant: "destructive",
      });
    },
  });

  const getRiskLevelColor = (level: string) => {
    switch (level) {
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'critical': return 'bg-red-100 text-red-800 border-red-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getRiskLevelText = (level: string) => {
    const texts = {
      ca: { low: 'Baix', medium: 'MitjÃ ', high: 'Alt', critical: 'CrÃ­tic' },
      es: { low: 'Bajo', medium: 'Medio', high: 'Alto', critical: 'CrÃ­tico' }
    };
    return texts[language as 'ca' | 'es'][level as keyof typeof texts.ca] || level;
  };

  const getEmpatheticTemplate = (level: string) => {
    const templates = {
      ca: {
        medium: `Hola {{name}},\n\nHem notat que Ãºltimament has tingut alguns retards o absÃ¨ncies. Volem recordar-te la importÃ ncia de la puntualitat per al bon funcionament del centre.\n\nSi tens alguna dificultat o necessites suport, no dubtis a contactar amb nosaltres. Estem aquÃ­ per ajudar-te.\n\nGrÃ cies per la teva comprensiÃ³.`,
        high: `Estimat/da {{name}},\n\nEns hem adonat que els teus registres d'assistÃ¨ncia mostren algunes irregularitats que ens preocupen. Volem treballar amb tu per trobar una soluciÃ³.\n\nT'agraÃ¯rÃ­em que ens expliquis si hi ha alguna circumstÃ ncia que estigui afectant la teva assistÃ¨ncia. El nostre objectiu Ã©s donar-te el suport que necessitis.\n\nEsperem la teva resposta per poder ajudar-te.`,
        critical: `{{name}},\n\nLes dades d'assistÃ¨ncia mostren una situaciÃ³ que requereix la nostra atenciÃ³ immediata. Ã‰s important que ens reunim per parlar sobre aquesta qÃ¼estiÃ³.\n\nContacta amb direcciÃ³ el mÃ©s aviat possible per concertar una reuniÃ³. Volem entendre la situaciÃ³ i trobar la millor manera d'avanÃ§ar junts.\n\nGrÃ cies per la teva colÂ·laboraciÃ³.`
      },
      es: {
        medium: `Hola {{name}},\n\nHemos notado que Ãºltimamente has tenido algunos retrasos o ausencias. Queremos recordarte la importancia de la puntualidad para el buen funcionamiento del centro.\n\nSi tienes alguna dificultad o necesitas apoyo, no dudes en contactar con nosotros. Estamos aquÃ­ para ayudarte.\n\nGracias por tu comprensiÃ³n.`,
        high: `Estimado/a {{name}},\n\nNos hemos dado cuenta de que tus registros de asistencia muestran algunas irregularidades que nos preocupan. Queremos trabajar contigo para encontrar una soluciÃ³n.\n\nTe agradecerÃ­amos que nos expliques si hay alguna circunstancia que estÃ© afectando tu asistencia. Nuestro objetivo es darte el apoyo que necesites.\n\nEsperamos tu respuesta para poder ayudarte.`,
        critical: `{{name}},\n\nLos datos de asistencia muestran una situaciÃ³n que requiere nuestra atenciÃ³n inmediata. Es importante que nos reunamos para hablar sobre esta cuestiÃ³n.\n\nContacta con direcciÃ³n lo antes posible para concertar una reuniÃ³n. Queremos entender la situaciÃ³n y encontrar la mejor manera de avanzar juntos.\n\nGracias por tu colaboraciÃ³n.`
      }
    };

    const template = templates[language as 'ca' | 'es'][level as keyof typeof templates.ca];
    return template || '';
  };

  const handleSendNotification = () => {
    if (!selectedEmployee || !notificationMessage.trim()) return;

    sendNotificationMutation.mutate({
      employeeId: selectedEmployee.employeeId,
      message: notificationMessage
    });
  };

  const handleUseTemplate = (employee: RiskAssessment) => {
    const template = getEmpatheticTemplate(employee.riskLevel);
    const personalizedMessage = template.replace(/\{\{name\}\}/g, employee.employeeName.split(' ')[0]);
    setNotificationMessage(personalizedMessage);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">
            {language === "ca" ? "AvaluaciÃ³ de Riscos" : "EvaluaciÃ³n de Riesgos"}
          </h2>
          <p className="text-muted-foreground">
            {language === "ca" 
              ? "Monitoratge proactiu del personal amb nivells de risc d'assistÃ¨ncia" 
              : "Monitoreo proactivo del personal con niveles de riesgo de asistencia"}
          </p>
        </div>
      </div>

      {/* Risk Level Summary */}
      <div className="grid grid-cols-4 gap-4">
        {['low', 'medium', 'high', 'critical'].map(level => {
          const count = Array.isArray(riskAssessments) ? riskAssessments.filter((r: RiskAssessment) => r.riskLevel === level).length : 0;
          return (
            <Card key={level}>
              <CardContent className="pt-6">
                <div className="flex items-center justify-between">
                  <Badge className={getRiskLevelColor(level)}>
                    {getRiskLevelText(level)}
                  </Badge>
                  <span className="text-2xl font-bold">{count}</span>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Risk Assessment List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            {language === "ca" ? "Personal per Nivell de Risc" : "Personal por Nivel de Riesgo"}
          </CardTitle>
          <CardDescription>
            {language === "ca" 
              ? "Llista ordenada per nivell de risc d'assistÃ¨ncia" 
              : "Lista ordenada por nivel de riesgo de asistencia"}
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {Array.isArray(riskAssessments) && riskAssessments.map((assessment: RiskAssessment) => (
              <div key={assessment.id} className="flex items-center justify-between p-4 border rounded-lg">
                <div className="flex items-center gap-4">
                  <Badge className={getRiskLevelColor(assessment.riskLevel)}>
                    {getRiskLevelText(assessment.riskLevel)}
                  </Badge>
                  <div>
                    <h3 className="font-medium">{assessment.employeeName}</h3>
                    <p className="text-sm text-muted-foreground">{assessment.employeeEmail}</p>
                  </div>
                </div>

                <div className="flex items-center gap-4">
                  <div className="text-right text-sm">
                    <div className="flex items-center gap-1 text-orange-600">
                      <Clock className="h-4 w-4" />
                      {assessment.delayMinutes} min {language === "ca" ? "retard" : "retraso"}
                    </div>
                    <div className="flex items-center gap-1 text-red-600">
                      <Calendar className="h-4 w-4" />
                      {assessment.absenceDays} {language === "ca" ? "dies absÃ¨ncia" : "dÃ­as ausencia"}
                    </div>
                  </div>

                  <Dialog open={isNotificationOpen && selectedEmployee?.id === assessment.id} 
                          onOpenChange={(open) => {
                            setIsNotificationOpen(open);
                            if (open) {
                              setSelectedEmployee(assessment);
                              handleUseTemplate(assessment);
                            } else {
                              setSelectedEmployee(null);
                              setNotificationMessage("");
                            }
                          }}>
                    <DialogTrigger asChild>
                      <Button variant="outline" size="sm">
                        <Send className="h-4 w-4 mr-2" />
                        {language === "ca" ? "Notificar" : "Notificar"}
                      </Button>
                    </DialogTrigger>
                    <DialogContent className="max-w-md">
                      <DialogHeader>
                        <DialogTitle>
                          {language === "ca" ? "Enviar NotificaciÃ³" : "Enviar NotificaciÃ³n"}
                        </DialogTitle>
                        <DialogDescription>
                          {language === "ca" 
                            ? `Enviant notificaciÃ³ a ${assessment.employeeName}` 
                            : `Enviando notificaciÃ³n a ${assessment.employeeName}`}
                        </DialogDescription>
                      </DialogHeader>

                      <div className="space-y-4">
                        <Alert>
                          <AlertTriangle className="h-4 w-4" />
                          <AlertDescription>
                            {language === "ca" 
                              ? "Plantilla empÃ tica precarregada basada en el nivell de risc" 
                              : "Plantilla empÃ¡tica precargada basada en el nivel de riesgo"}
                          </AlertDescription>
                        </Alert>

                        <Textarea
                          placeholder={language === "ca" ? "Missatge personalitzat..." : "Mensaje personalizado..."}
                          value={notificationMessage}
                          onChange={(e) => setNotificationMessage(e.target.value)}
                          rows={8}
                        />

                        <div className="flex justify-end gap-2">
                          <Button variant="outline" onClick={() => setIsNotificationOpen(false)}>
                            {language === "ca" ? "CancelÂ·lar" : "Cancelar"}
                          </Button>
                          <Button 
                            onClick={handleSendNotification}
                            disabled={sendNotificationMutation.isPending || !notificationMessage.trim()}
                          >
                            {sendNotificationMutation.isPending ? (
                              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white" />
                            ) : (
                              <Send className="h-4 w-4 mr-2" />
                            )}
                            {language === "ca" ? "Enviar" : "Enviar"}
                          </Button>
                        </div>
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

