                    </Button>
                    <Button type="submit" disabled={sendAlertMutation.isPending}>
                      <Send className="h-4 w-4 mr-2" />
                      {sendAlertMutation.isPending ? "Enviant..." : "Enviar Alerta"}
                    </Button>
                  </div>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center gap-4">
