import { 
  Shield, 
  Search, 
  Eye, 
  Check, 
  X,
  Clock,
  AlertTriangle,
  FileText
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface PrivacyRequest {
  id: string;
  userId: string;
  userName: string;
  userEmail: string;
  requestType: string;
  description: string;
  status: string;
  adminResponse?: string;
  assignedTo?: string;
  assignedToName?: string;
  dueDate?: string;
  resolvedAt?: string;
  createdAt: string;
}

export function PrivacyManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [typeFilter, setTypeFilter] = useState("all");
  const [selectedRequest, setSelectedRequest] = useState<PrivacyRequest | null>(null);
  const [responseDialogOpen, setResponseDialogOpen] = useState(false);

  // Fetch privacy requests
  const { data: privacyRequests = [], isLoading } = useQuery({
    queryKey: ['/api/admin/privacy-requests', user?.institutionId],
    enabled: !!user?.institutionId,
  });

  // Filter requests
  const filteredRequests = privacyRequests.filter((request: PrivacyRequest) => {
    const matchesSearch = 
      request.userName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.userEmail.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.description.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === "all" || request.status === statusFilter;
    const matchesType = typeFilter === "all" || request.requestType === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  // Update request status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async (data: {
      requestId: string;
      status: string;
      adminResponse: string;
    }) => {
      return apiRequest(`/api/admin/privacy-requests/${data.requestId}/status`, {
        method: 'PUT',
        body: JSON.stringify({
          status: data.status,
          adminResponse: data.adminResponse,
        }),
      });
    },
    onSuccess: () => {
      toast({
        title: "SolÂ·licitud actualitzada",
        description: "L'estat de la solÂ·licitud s'ha actualitzat correctament.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/privacy-requests'] });
      setResponseDialogOpen(false);
      setSelectedRequest(null);
    },
