  const getTypeName = (type: string) => {
    switch (type) {
      case 'access': return 'AccÃ©s a Dades';
      case 'rectification': return 'RectificaciÃ³';
      case 'deletion': return 'EliminaciÃ³';
      case 'portability': return 'Portabilitat';
