                <p className="text-muted-foreground">En TrÃ mit</p>
              </div>
            </div>
          </Card>

          <Card className="p-3">
            <div className="flex items-center gap-2">
              <Check className="h-4 w-4 text-green-500" />
              <div className="text-sm">
                <p className="font-semibold">
                  {filteredRequests.filter((r: PrivacyRequest) => r.status === 'resolved').length}
                </p>
                <p className="text-muted-foreground">Resoltes</p>
              </div>
            </div>
          </Card>

          <Card className="p-3">
            <div className="flex items-center gap-2">
              <X className="h-4 w-4 text-red-500" />
              <div className="text-sm">
                <p className="font-semibold">
                  {filteredRequests.filter((r: PrivacyRequest) => 
                    r.dueDate && isOverdue(r.dueDate)
                  ).length}
                </p>
                <p className="text-muted-foreground">VenÃ§udes</p>
              </div>
            </div>
          </Card>
        </div>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center gap-4">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Cercar per usuari o descripciÃ³..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-9"
              />
            </div>

            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filtrar per estat" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Tots els estats</SelectItem>
                <SelectItem value="initiated">Iniciades</SelectItem>
                <SelectItem value="in_progress">En TrÃ mit</SelectItem>
                <SelectItem value="resolved">Resoltes</SelectItem>
                <SelectItem value="rejected">Rebutjades</SelectItem>
              </SelectContent>
            </Select>

            <Select value={typeFilter} onValueChange={setTypeFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filtrar per tipus" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Tots els tipus</SelectItem>
                <SelectItem value="access">AccÃ©s a Dades</SelectItem>
                <SelectItem value="rectification">RectificaciÃ³</SelectItem>
                <SelectItem value="deletion">EliminaciÃ³</SelectItem>
                <SelectItem value="portability">Portabilitat</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Privacy Requests Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Shield className="h-5 w-5" />
            SolÂ·licituds de Privacitat ({filteredRequests.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Tipus SolÂ·licitud</TableHead>
                  <TableHead>Usuari</TableHead>
                  <TableHead>DescripciÃ³</TableHead>
                  <TableHead>Estat</TableHead>
                  <TableHead>Data CreaciÃ³</TableHead>
                  <TableHead>Data LÃ­mit</TableHead>
                  <TableHead>Accions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredRequests.map((request: PrivacyRequest) => (
                  <TableRow key={request.id}>
                    <TableCell>
                      <Badge className={getTypeColor(request.requestType)}>
                        {getTypeName(request.requestType)}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div>
                        <p className="font-medium">{request.userName}</p>
                        <p className="text-sm text-muted-foreground">{request.userEmail}</p>
                      </div>
                    </TableCell>
                    <TableCell className="max-w-xs">
                      <div className="truncate" title={request.description}>
                        {request.description}
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge className={getStatusColor(request.status)}>
                        {getStatusName(request.status)}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      {new Date(request.createdAt).toLocaleDateString('ca-ES')}
                    </TableCell>
                    <TableCell>
                      {request.dueDate && (
                        <div className={`${isOverdue(request.dueDate) ? 'text-red-600' : ''}`}>
                          {new Date(request.dueDate).toLocaleDateString('ca-ES')}
                          {isOverdue(request.dueDate) && (
                            <AlertTriangle className="inline h-4 w-4 ml-1" />
                          )}
                        </div>
                      )}
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleResponse(request)}
                          disabled={request.status === 'resolved'}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}

                {filteredRequests.length === 0 && (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center text-muted-foreground py-8">
                      No s'han trobat solÂ·licituds amb els filtres aplicats
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Response Dialog */}
      {selectedRequest && (
        <Dialog open={responseDialogOpen} onOpenChange={setResponseDialogOpen}>
          <DialogContent className="max-w-2xl modal-content-solid">
            <DialogHeader>
              <DialogTitle>Respondre SolÂ·licitud de Privacitat</DialogTitle>
              <DialogDescription>
                Gestiona la resposta a la solÂ·licitud de {selectedRequest.userName}
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4">
              {/* Request Details */}
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Detalls de la SolÂ·licitud</CardTitle>
                </CardHeader>
                <CardContent className="space-y-2">
                  <div><strong>Tipus:</strong> {getTypeName(selectedRequest.requestType)}</div>
                  <div><strong>Usuari:</strong> {selectedRequest.userName} ({selectedRequest.userEmail})</div>
                  <div><strong>DescripciÃ³:</strong> {selectedRequest.description}</div>
                  <div><strong>Data CreaciÃ³:</strong> {new Date(selectedRequest.createdAt).toLocaleString('ca-ES')}</div>
                  {selectedRequest.dueDate && (
                    <div><strong>Data LÃ­mit:</strong> {new Date(selectedRequest.dueDate).toLocaleString('ca-ES')}</div>
                  )}
                </CardContent>
              </Card>

              {/* Response Form */}
              <form onSubmit={(e) => {
                e.preventDefault();
                const formData = new FormData(e.currentTarget);
                updateStatusMutation.mutate({
                  requestId: selectedRequest.id,
                  status: formData.get('status') as string,
                  adminResponse: formData.get('adminResponse') as string,
                });
              }}>
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium">Nou Estat</label>
                    <Select name="status" required defaultValue={selectedRequest.status}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="initiated">Iniciada</SelectItem>
                        <SelectItem value="in_progress">En TrÃ mit</SelectItem>
                        <SelectItem value="resolved">Resolta</SelectItem>
                        <SelectItem value="rejected">Rebutjada</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="text-sm font-medium">Resposta Administrativa</label>
                    <Textarea 
                      name="adminResponse" 
                      placeholder="Escriu la resposta o explicaciÃ³ per a l'usuari..."
                      rows={4}
                      defaultValue={selectedRequest.adminResponse || ''}
                    />
                  </div>

                  <div className="flex justify-end gap-2">
                    <Button type="button" variant="outline" onClick={() => setResponseDialogOpen(false)}>
                      CancelÂ·lar
                    </Button>
                    <Button type="submit" disabled={updateStatusMutation.isPending}>
                      {updateStatusMutation.isPending ? "Guardant..." : "Guardar Resposta"}
                    </Button>
                  </div>
                </div>
              </form>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

export default PrivacyManagement;

