import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  FileText, 
  Download, 
  Calendar, 
  Users, 
  TrendingUp,
  Filter
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { WeeklyScheduleModal } from "./WeeklyScheduleModal";

export function ReportsManagement() {
  const { user } = useAuth();
  const { toast } = useToast();

  const [selectedEmployees, setSelectedEmployees] = useState<string[]>([]);
  const [dateRange, setDateRange] = useState({
    start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    end: new Date().toISOString().split('T')[0]
  });
  const [reportType, setReportType] = useState("attendance");
  const [showWeeklyDetailModal, setShowWeeklyDetailModal] = useState(false);
  const [selectedUserForDetail, setSelectedUserForDetail] = useState<string | null>(null);

  // Define interfaces for type safety
  interface Employee {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  }

  interface WeeklyEmployeeData {
    id: string;
    name: string;
    email: string;
    complianceRate?: number;
    weeklyDetails?: Array<{
      status: 'present' | 'late' | 'absent' | 'unknown';
      actualHours?: number;
      scheduledHours?: number;
    }>;
  }

  interface ReportGenerationResponse {
    success: boolean;
    reportData: Array<{
      id: string;
      name: string;
      email: string;
      total_records: number;
      avg_hours: number;
      total_delays: number;
      days_present: number;
      missing_exits: number;
    }>;
    reportType: string;
    dateRange: { start: string; end: string };
    employeeCount: number;
    generatedAt: string;
  }

  // Fetch weekly attendance data for all employees
  const { data: weeklyData = [], isLoading: weeklyLoading } = useQuery<WeeklyEmployeeData[]>({
    queryKey: ['/api/admin/weekly-attendance', user?.institutionId],
    enabled: !!user?.institutionId && showWeeklyDetailModal,
    retry: 1,
    refetchOnWindowFocus: false,
  });

  // Fetch employees for selection
  const { data: employees = [] } = useQuery<Employee[]>({
    queryKey: ['/api/admin/employees', user?.institutionId],
    enabled: !!user?.institutionId,
  });

  const handleEmployeeSelection = (employeeId: string, checked: boolean) => {
    if (checked) {
      setSelectedEmployees([...selectedEmployees, employeeId]);
    } else {
      setSelectedEmployees(selectedEmployees.filter(id => id !== employeeId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedEmployees(['all']);
    } else {
      setSelectedEmployees([]);
    }
  };

  const isAllSelected = selectedEmployees.includes('all');
  const isIndeterminate = selectedEmployees.length > 0 && !isAllSelected;

  // Generate and download report
  const generateReport = () => {
    if (!user?.institutionId) {
      toast({
        title: "Error",
        description: "No s'ha pogut identificar la instituciÃ³",
        variant: "destructive"
      });
      return;
    }

    if (selectedEmployees.length === 0) {
      toast({
        title: "Error", 
        description: "Selecciona almenys un empleat per generar l'informe",
        variant: "destructive"
      });
      return;
    }

    const employeeParam = selectedEmployees.includes('all') ? 'all' : selectedEmployees.join(',');

    const params = new URLSearchParams({
      institutionId: user.institutionId,
      employees: employeeParam,
      startDate: dateRange.start,
      endDate: dateRange.end,
      type: reportType
    });

    // Open download in new window
    const downloadUrl = `/api/admin/reports/generate?${params}`;
    window.open(downloadUrl, '_blank');

    toast({
      title: "Informe generat",
      description: "L'informe s'estÃ  descarregant...",
    });
  };

  // Generate custom report data (for preview/analysis)
  const generateCustomReportMutation = useMutation({
    mutationFn: async (): Promise<ReportGenerationResponse> => {
      const response = await fetch('/api/admin/reports/generate-custom', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          selectedEmployees,
          reportType,
          dateRange,
          institutionId: user?.institutionId
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Error generant informe');
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Informe generat",
        description: `S'han processat ${data.employeeCount} empleats correctament`,
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Error generant informe",
        variant: "destructive"
      });
    }
  });

  const presetReports = [
    {
      id: 'monthly_attendance',
      name: 'Informe Mensual d\'AssistÃ¨ncia',
      description: 'Resum d\'assistÃ¨ncia de tots els empleats del mes actual',
      type: 'attendance'
    },
    {
      id: 'weekly_summary',
      name: 'Informe Setmanal',
      description: 'Llista d\'usuaris amb fitxatges reals vs horaris previstos',
      type: 'weekly_detailed'
    },
    {
      id: 'employee_performance',
      name: 'Rendiment per Empleat',
      description: 'AnÃ lisi detallada del rendiment individual',
      type: 'performance'
    },
    {
      id: 'delay_analysis',
      name: 'AnÃ lisi de Retards',
      description: 'Informe de retards i absÃ¨ncies injustificades',
      type: 'delays'
    }
  ];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">GestiÃ³ d'Informes</h2>
          <p className="text-muted-foreground">
            Genera informes personalitzats i estadÃ­stiques del centre
          </p>
        </div>
      </div>

      {/* Quick Reports */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Informes Predefinits
          </CardTitle>
          <CardDescription>
            Genera rÃ pidament informes amb configuracions predeterminades
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {presetReports.map((report) => (
              <Card key={report.id} className="hover:shadow-md transition-shadow">
                <CardHeader className="pb-2">
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">{report.name}</CardTitle>
                    <Badge variant="outline">{report.type}</Badge>
                  </div>
                  <CardDescription>{report.description}</CardDescription>
                </CardHeader>
                <CardContent>
                  <Button 
                    size="sm" 
                    className="w-full"
                    onClick={() => {
                      if (report.id === 'weekly_summary') {
                        setShowWeeklyDetailModal(true);
                      } else {
                        setReportType(report.type);
                        generateReport();
                      }
                    }}
                    data-testid={`button-generate-${report.id}`}
                  >
                    {report.id === 'weekly_summary' ? (
                      <>
                        <Users className="h-4 w-4 mr-2" />
                        Veure Llista Usuaris
                      </>
                    ) : (
                      <>
                        <Download className="h-4 w-4 mr-2" />
                        Generar
                      </>
                    )}
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Custom Report Generator */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Generador d'Informes Personalitzats
          </CardTitle>
          <CardDescription>
            Selecciona empleats especÃ­fics i personalitza el perÃ­ode de l'informe
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Date Range Selection */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="text-sm font-medium">Data Inici</label>
              <Input
                type="date"
                value={dateRange.start}
                onChange={(e) => setDateRange({ ...dateRange, start: e.target.value })}
              />
            </div>
            <div>
              <label className="text-sm font-medium">Data Final</label>
              <Input
                type="date"
                value={dateRange.end}
                onChange={(e) => setDateRange({ ...dateRange, end: e.target.value })}
              />
            </div>
            <div>
              <label className="text-sm font-medium">Tipus d'Informe</label>
              <Select value={reportType} onValueChange={setReportType}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="attendance">AssistÃ¨ncia</SelectItem>
                  <SelectItem value="summary">Resum</SelectItem>
                  <SelectItem value="performance">Rendiment</SelectItem>
                  <SelectItem value="delays">Retards</SelectItem>
                  <SelectItem value="detailed">Detallat</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Employee Selection */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <label className="text-sm font-medium">
                Seleccionar Empleats ({isAllSelected ? 'Tots' : selectedEmployees.length} seleccionats)
              </label>
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="select-all"
                    checked={isAllSelected}
                    onCheckedChange={handleSelectAll}
                    data-testid="checkbox-select-all"
                  />
                  <label htmlFor="select-all" className="text-sm cursor-pointer">
                    Seleccionar tots els empleats
                  </label>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setSelectedEmployees([])}
                  disabled={selectedEmployees.length === 0}
                  data-testid="button-clear-selection"
                >
                  Esborrar selecciÃ³
                </Button>
              </div>
            </div>

            {!isAllSelected && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-2 max-h-64 overflow-y-auto border rounded p-4">
                {(employees as Employee[]).map((employee: Employee) => (
                  <div key={employee.id} className="flex items-center space-x-2">
                    <Checkbox
                      id={employee.id}
                      checked={selectedEmployees.includes(employee.id)}
                      onCheckedChange={(checked) => 
                        handleEmployeeSelection(employee.id, checked as boolean)
                      }
                      data-testid={`checkbox-employee-${employee.id}`}
                    />
                    <label
                      htmlFor={employee.id}
                      className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer"
                    >
                      {employee.firstName} {employee.lastName}
                      <span className="text-muted-foreground block text-xs font-normal">
                        {employee.email}
                      </span>
                    </label>
                  </div>
                ))}
              </div>
            )}

            {isAllSelected && (
              <div className="border rounded p-4 bg-muted/20">
                <p className="text-sm text-muted-foreground text-center">
                  S'han seleccionat tots els empleats ({(employees as Employee[]).length} empleats)
                </p>
              </div>
            )}
          </div>

          {/* Generate Button */}
          <div className="flex justify-end">
            <Button
              onClick={generateReport}
              disabled={selectedEmployees.length === 0}
              className="min-w-32"
              data-testid="button-generate-report"
            >
              <Download className="h-4 w-4 mr-2" />
              Generar Informe ({isAllSelected ? 'Tots els empleats' : `${selectedEmployees.length} seleccionats`})
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Recent Reports */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            Informes Recents
          </CardTitle>
          <CardDescription>
            Descarrega informes generats anteriorment
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="text-center text-muted-foreground py-8">
