              ) : (
                <div className="space-y-2">
                  {/* Day headers - Only weekdays */}
                  <div className="grid grid-cols-5 gap-1 mb-2">
                    {['L', 'M', 'X', 'J', 'V'].map(day => (
                      <div key={day} className="p-2 text-center text-xs font-medium text-muted-foreground">
