      toast({
        title: t("success", language),
        description: language === "ca" ? "MigraciÃ³ iniciada correctament" : "MigraciÃ³n iniciada correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/academic-years/migrations"] });
