                    return (
                      <div key={migration.id} className="border rounded-lg p-4 space-y-3">
                        <div className="flex justify-between items-start">
                          <div className="space-y-1">
                            <div className="flex items-center gap-2">
                              <span className="font-medium">
                                {sourceYear?.name} â†’ {targetYear?.name}
                              </span>
                              <Badge className={getStatusColor(migration.status)}>
                                {migration.status === 'completed' && (language === "ca" ? 'Completada' : 'Completada')}
                                {migration.status === 'in_progress' && (language === "ca" ? 'En ProgrÃ©s' : 'En Progreso')}
                                {migration.status === 'failed' && (language === "ca" ? 'Fallida' : 'Fallida')}
                                {migration.status === 'pending' && (language === "ca" ? 'Pendent' : 'Pendiente')}
                              </Badge>
                            </div>
                            <p className="text-sm text-gray-600">
                              {language === "ca" ? "Tipus: " : "Tipo: "}{migrationTypeOptions.find(opt => opt.id === migration.migrationType)?.label || migration.migrationType}
                            </p>
                            <p className="text-xs text-gray-500">
                              {language === "ca" ? "Iniciada: " : "Iniciada: "}{new Date(migration.createdAt).toLocaleString('es-ES')}
                              {migration.completedAt && (
                                <> â€¢ {language === "ca" ? "Completada: " : "Completada: "}{new Date(migration.completedAt).toLocaleString('es-ES')}</>
                              )}
                            </p>
                          </div>

                          <div className="flex gap-2">
                            <Button 
                              variant="outline" 
                              size="sm"
                              onClick={() => deleteMigrationMutation.mutate(migration.id)}
                              disabled={migration.status === 'in_progress' || deleteMigrationMutation.isPending}
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>

                        {migration.status === 'in_progress' && (
                          <div className="space-y-2">
                            <div className="flex justify-between text-sm">
                              <span>Progreso</span>
                              <span>{migration.migratedRecords}/{migration.totalRecords} registros</span>
                            </div>
                            <Progress value={progressPercentage} className="w-full" />
                          </div>
                        )}

                        {migration.status === 'completed' && (
                          <div className="text-sm text-green-600">
                            âœ“ {migration.migratedRecords} registros migrados exitosamente
                          </div>
                        )}

                        {migration.status === 'failed' && migration.errors && migration.errors.length > 0 && (
                          <div className="bg-red-50 border border-red-200 rounded p-3">
                            <p className="text-sm font-medium text-red-800 mb-2">Errores:</p>
                            <ul className="text-sm text-red-700 space-y-1">
                              {migration.errors.slice(0, 3).map((error, idx) => (
                                <li key={idx}>â€¢ {error.message || error}</li>
                              ))}
                              {migration.errors.length > 3 && (
                                <li>â€¢ Y {migration.errors.length - 3} errores mÃ¡s...</li>
                              )}
                            </ul>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

