import { useState, useRef, useEffect } from 'react';
import { useMutation } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Link } from 'wouter';
import { 
  QrCode, 
  Camera, 
  Scan, 
  Clock, 
  CheckCircle, 
  XCircle,
  Timer,
  User,
  Smartphone,
  AlertTriangle,
  LogIn,
  LogOut
} from 'lucide-react';

export default function PublicQRAttendance() {
  console.log("ðŸš€ PublicQRAttendance component mounted/re-rendered at:", new Date().toISOString());

  const { toast } = useToast();

  const [qrInput, setQrInput] = useState('');
  const [processing, setProcessing] = useState(false);
  const [lastResult, setLastResult] = useState<any>(null);
  const [showResult, setShowResult] = useState(false);
  const [displayTime, setDisplayTime] = useState(new Date());

  // Add a timer like in App.tsx for the clock
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    console.log("ðŸ• CLOCK EFFECT: Setting up timer");
    // Start the clock timer
    timerRef.current = setInterval(() => {
      const newTime = new Date();
      // TEMPORALLY DISABLE CLOCK LOGS TO REDUCE CONSOLE SPAM
      // console.log("â° CLOCK UPDATE:", newTime.toLocaleTimeString());
      setDisplayTime(newTime);
    }, 1000);

    return () => {
      console.log("ðŸ• CLOCK EFFECT: Cleaning up timer");
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  // QR Processing mutation
  const processQRMutation = useMutation({
    mutationFn: async (qrData: string) => {
      const currentTimestamp = new Date();
      console.log("ðŸ• FRONTEND QR TIMESTAMP DEBUG:");
      console.log("  Frontend current time:", currentTimestamp.toISOString());
      console.log("  Frontend local time:", currentTimestamp.toLocaleString('es-ES', { timeZone: 'Europe/Madrid' }));
      console.log("  QR Data:", qrData.trim());

      const response = await apiRequest("POST", "/api/attendance/qr-process", {
        qrData: qrData.trim(),
        timestamp: currentTimestamp.toISOString(),
        location: window.location.origin
      });

      // Parse JSON if it's a Response object
      const data = response instanceof Response ? await response.json() : response;
      console.log("âœ… QR RESPONSE PARSED:", data);
      return data;
    },
    onSuccess: (data) => {
      console.log("ðŸŽ‰ QR SUCCESS DATA:", JSON.stringify(data, null, 2));
      setLastResult(data);
      setShowResult(true);

      // DEBUG LOGS FOR MODAL ISSUE
      console.log("ðŸ” TOAST DEBUG - Success toast about to show");
      console.log("ðŸ” TOAST DEBUG - data.type:", data.type);
      console.log("ðŸ” TOAST DEBUG - data.employeeName:", data.employeeName);

      toast({
        title: "Fitxatge registrat",
        description: `${data.type === 'check_in' ? 'Entrada' : 'Sortida'} registrada correctament per ${data.employeeName}`,
      });

      console.log("ðŸ” TOAST DEBUG - Toast called, clearing form");
      setQrInput('');
      setProcessing(false);

      // Hide result after 5 seconds
      setTimeout(() => {
        console.log("ðŸ” TIMEOUT DEBUG - Hiding result after 5 seconds");
        setShowResult(false);
      }, 5000);
    },
    onError: (error: any) => {
      console.log("âŒ QR ERROR:", error);
      console.log("âŒ Error details:", JSON.stringify(error, null, 2));

      // DEBUG LOGS FOR MODAL ISSUE  
      console.log("ðŸ” TOAST DEBUG - Error toast about to show");
      console.log("ðŸ” TOAST DEBUG - error.message:", error.message);

      setLastResult({ error: true, message: error.message });
      setShowResult(true);
      toast({
        title: "Error",
        description: error.message || 'Error al processar el codi QR',
        variant: "destructive",
      });
      setProcessing(false);

      // Hide error after 5 seconds
      setTimeout(() => {
        console.log("ðŸ” TIMEOUT DEBUG - Hiding error after 5 seconds");
        setShowResult(false);
      }, 5000);
    },
  });

  const handleQRSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    console.log("ðŸŽ¯ QR FORM SUBMITTED!");
    console.log("ðŸŽ¯ QR Input:", qrInput);

    if (!qrInput.trim()) {
      console.log("ðŸŽ¯ Empty QR input!");
      toast({
        title: "Error",
        description: "Introdueix un codi QR vÃ lid",
        variant: "destructive",
      });
      return;
    }

    console.log("ðŸŽ¯ Starting QR processing...");
    setProcessing(true);
    // Update display time when processing starts
    setDisplayTime(new Date());
    processQRMutation.mutate(qrInput);
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('ca-ES', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const formatDate = (date: Date) => {
    return date.toLocaleDateString('ca-ES', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md space-y-6">
        {/* Header */}
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">fich.ai</h1>
          <h2 className="text-xl font-semibold text-gray-700 mb-1">Fitxatge per Codi QR</h2>
          <p className="text-sm text-gray-600">Sistema de control d'assistÃ¨ncia</p>
