  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [isScanning, setIsScanning] = useState(false);
  const [scannedCode, setScannedCode] = useState('');
  const [lastResult, setLastResult] = useState<any>(null);
  const [manualInput, setManualInput] = useState('');
  const [showManualInput, setShowManualInput] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);
  const [currentTime, setCurrentTime] = useState(new Date());

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // QR Processing mutation - uses authenticated session
  const processQRMutation = useMutation({
    mutationFn: async (qrData: string) => {
      console.log("ðŸ”¥ PROCESSING QR WITH USER SESSION:", user?.id);

      // Extract employee ID from QR code (format: userId-YYYY-MM-DD)
      const qrParts = qrData.trim().split('-');
      if (qrParts.length < 4) {
        throw new Error('Format del codi QR no vÃ lid');
      }

      const userIdParts = qrParts.slice(0, -3);
      const employeeId = userIdParts.join('-');
      const qrDate = `${qrParts[qrParts.length-3]}-${qrParts[qrParts.length-2]}-${qrParts[qrParts.length-1]}`;
      const todayDate = new Date().toISOString().split('T')[0];

      // Validate QR is from today
      if (qrDate !== todayDate) {
        throw new Error('Aquest codi QR ha caducat. Genera un nou codi QR.');
      }

      // SECURITY: Only allow user to scan their own QR code
      if (employeeId !== user?.id) {
        throw new Error('NomÃ©s pots utilitzar el teu propi codi QR personal segons la normativa vigent.');
      }

      // Process the QR code with the authenticated session
      return await apiRequest("POST", "/api/attendance/qr-process", {
        qrData: qrData.trim(),
        timestamp: new Date().toISOString(),
        location: window.location.origin
      });
    },
    onSuccess: (data: any) => {
      console.log("âœ… QR PROCESSED SUCCESSFULLY:", data);
      setLastResult(data);

      toast({
        title: "Fitxatge Registrat!",
        description: `${data.type === 'check_in' ? 'Entrada' : 'Sortida'} registrada correctament`,
      });

      // Invalidate queries to refresh data
      queryClient.invalidateQueries({ queryKey: ["/api/attendance", user?.id] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });

      // Clear inputs
      setScannedCode('');
      setManualInput('');
    },
    onError: (error: any) => {
      console.error('âŒ QR PROCESSING ERROR:', error);
      toast({
        title: "Error",
        description: error.message || 'Error processant el codi QR',
        variant: "destructive",
      });
    },
  });

  // Start camera for scanning
  const startScanning = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: 'environment' } // Use back camera on mobile
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsScanning(true);
      }
    } catch (error) {
      console.error('Error accessing camera:', error);
      toast({
        title: "Error de CÃ mera",
        description: "No es pot accedir a la cÃ mera. Utilitza l'entrada manual.",
        variant: "destructive",
      });
      setShowManualInput(true);
    }
  };

  // Stop camera
  const stopScanning = () => {
    if (videoRef.current?.srcObject) {
      const stream = videoRef.current.srcObject as MediaStream;
      stream.getTracks().forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setIsScanning(false);
  };

  // Process manual input
  const processManualInput = () => {
    if (!manualInput.trim()) {
      toast({
        title: "Error",
        description: "Introdueix un codi QR vÃ lid",
        variant: "destructive",
      });
      return;
    }

    processQRMutation.mutate(manualInput.trim());
  };

  // Generate today's QR for the current user
  const generateMyQR = () => {
    if (!user?.id) return '';
    const today = new Date().toISOString().split('T')[0];
    return `${user.id}-${today}`;
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('ca-ES', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  return (
    <div className="min-h-screen bg-background p-4">
      {/* Header */}
      <div className="mb-6">
        <div className="flex items-center gap-3 mb-4">
          <Link href="/dashboard">
            <Button variant="ghost" size="sm">
              <ArrowLeft className="h-4 w-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-bold text-foreground">EscÃ ner QR MÃ²bil</h1>
            <p className="text-sm text-muted-foreground">Apunta al teu codi QR per fitxar</p>
          </div>
        </div>

        {/* Current Time */}
        <Card className="bg-primary/5 border-primary/20">
          <CardContent className="text-center p-4">
            <div className="text-2xl font-mono font-bold text-primary">
              {formatTime(currentTime)}
            </div>
            <div className="text-sm text-muted-foreground">
              {currentTime.toLocaleDateString('ca-ES', {
                weekday: 'long',
                day: 'numeric',
                month: 'long'
              })}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* User Info */}
      <Card className="mb-4">
        <CardContent className="p-4">
          <div className="flex items-center gap-3">
            <User className="h-5 w-5 text-primary" />
            <div>
              <div className="font-medium">{user?.firstName} {user?.lastName}</div>
              <div className="text-sm text-muted-foreground">{user?.email}</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Success Result */}
      {lastResult && (
        <Alert className="mb-4 border-green-200 bg-green-50">
          <CheckCircle className="h-4 w-4" />
          <AlertDescription className="text-green-800">
            <strong>Fitxatge registrat!</strong> {lastResult.type === 'check_in' ? 'Entrada' : 'Sortida'} a les {new Date(lastResult.timestamp).toLocaleTimeString('ca-ES')}
          </AlertDescription>
        </Alert>
      )}

      {/* Scanner Section */}
      <Card className="mb-4">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <QrCode className="h-5 w-5" />
            Escanejar Codi QR
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Camera Scanner */}
          {!isScanning ? (
            <div className="text-center space-y-4">
              <Button 
                onClick={startScanning}
