import { useState, useRef, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';
import { useLanguage } from '@/hooks/useLanguage';
import { useToast } from '@/hooks/use-toast';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Input } from '@/components/ui/input';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { 
  QrCode, 
  Camera, 
  Download, 
  Printer, 
  User,
  Building2,
  Info,
  Copy,
  Clock,
  UserCheck,
  ArrowLeft,
  AlertTriangle,
  Scan,
  Eye,
  EyeOff
} from 'lucide-react';
import { Link } from 'wouter';
import QRCodeLib from 'qrcode';
import { queryClient } from '@/lib/queryClient';

export default function UnifiedQR() {
  const { user } = useAuth();
  const { language } = useLanguage();
  const { toast } = useToast();

  // QR Generation states
  const [qrDataUrl, setQrDataUrl] = useState<string>('');
  const [qrCanvas, setQrCanvas] = useState<HTMLCanvasElement | null>(null);

  // QR Scanning states
  const [isScanning, setIsScanning] = useState(false);
  const [showManualInput, setShowManualInput] = useState(false);
  const [scannedCode, setScannedCode] = useState('');
  const [manualInput, setManualInput] = useState('');
  const [lastResult, setLastResult] = useState<any>(null);

  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Get current attendance status
  const { data: attendance = [] } = useQuery<any[]>({
    queryKey: ["/api/attendance", user?.id],
    enabled: !!user?.id,
    refetchInterval: 30000,
    refetchOnWindowFocus: true,
  });

  // Generate today's QR for the current user
  const generateMyQR = () => {
    if (!user?.id) return '';
    const today = new Date().toISOString().split('T')[0];
    return `${user.id}-${today}`;
  };

  // Generate QR Code
  useEffect(() => {
    const generateQR = async () => {
      if (!user) return;

      try {
        const qrData = generateMyQR();
        const canvas = document.createElement('canvas');

        await QRCodeLib.toCanvas(canvas, qrData, {
          width: 300,
          margin: 2,
          color: {
            dark: '#000000',
            light: '#FFFFFF'
          }
        });

        const dataUrl = canvas.toDataURL('image/png');
        setQrDataUrl(dataUrl);
        setQrCanvas(canvas);
      } catch (error) {
        console.error('Error generating QR:', error);
      }
    };

    generateQR();
  }, [user]);

  // Process QR mutation
  const processQRMutation = useMutation({
    mutationFn: async (qrCode: string) => {
      const response = await fetch('/api/attendance/qr-process', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          qrData: qrCode.trim(),
          timestamp: new Date().toISOString(),
          location: window.location.origin
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Error processant QR');
      }

      return response.json();
    },
    onSuccess: (data: any) => {
      console.log("âœ… QR PROCESSED SUCCESSFULLY:", data);
      setLastResult(data);

      toast({
        title: "Fitxatge Registrat!",
        description: `${data.type === 'check_in' ? 'Entrada' : 'Sortida'} registrada correctament a les ${new Date().toLocaleTimeString('ca-ES')}`,
      });

      // Invalidate queries to refresh data
      queryClient.invalidateQueries({ queryKey: ["/api/attendance", user?.id] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });

      // Clear inputs
      setScannedCode('');
      setManualInput('');

      // Stop camera after successful scan
      if (isScanning) {
        stopScanning();
      }
    },
    onError: (error: any) => {
      console.error('âŒ QR PROCESSING ERROR:', error);
      toast({
        title: "Error",
        description: error.message || 'Error processant el codi QR',
        variant: "destructive",
      });
    },
  });

  // Start camera for scanning
  const startScanning = async () => {
    try {
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Camera not supported');
      }

      console.log('ðŸŽ¥ Requesting camera access...');

      // Detect iOS Safari
      const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent);
      const isSafari = /Safari/.test(navigator.userAgent) && !/Chrome/.test(navigator.userAgent);

      console.log('ðŸ“± Device detection:', { isIOS, isSafari });

      // iOS Safari specific constraints
      let constraints;
      if (isIOS && isSafari) {
        constraints = { 
          video: { 
            facingMode: { ideal: 'environment' },
            width: { ideal: 640, max: 1920 },
            height: { ideal: 480, max: 1080 },
            frameRate: { ideal: 30, max: 30 }
          },
          audio: false
        };
      } else {
        constraints = { 
          video: { 
            facingMode: 'environment',
            width: { ideal: 1280 },
            height: { ideal: 720 }
          } 
        };
      }

      let stream;
      try {
        console.log('ðŸŽ¥ Trying with environment camera...');
        stream = await navigator.mediaDevices.getUserMedia(constraints);
      } catch (envError) {
        console.log('ðŸŽ¥ Environment camera failed, trying any camera...', envError);
        const fallbackConstraints = { 
          video: { 
            width: { ideal: 640 },
            height: { ideal: 480 }
          } 
        };
        stream = await navigator.mediaDevices.getUserMedia(fallbackConstraints);
      }

      console.log('ðŸŽ¥ Camera access granted!');

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsScanning(true);

        // iOS Safari specific handling
        if (isIOS && isSafari) {
          videoRef.current.setAttribute('playsinline', 'true');
          videoRef.current.setAttribute('webkit-playsinline', 'true');
          videoRef.current.muted = true;
        }

        // Wait for video to load
        videoRef.current.onloadedmetadata = () => {
          console.log('ðŸŽ¥ Video metadata loaded');
          if (videoRef.current) {
            videoRef.current.play().catch(playError => {
              console.error('Play error:', playError);
            });
          }
        };
      }
    } catch (error: any) {
      console.error('âŒ Camera error:', error);
      let errorMessage = "No es pot accedir a la cÃ mera.";

      if (error.name === 'NotAllowedError') {
        errorMessage = "Permisos de cÃ mera denegats. A iOS, ves a ConfiguraciÃ³ > Safari > CÃ mera i permet l'accÃ©s.";
